package neveen;

public class relational_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//relational operator
		//<,>,<=,>=,==
		int a=9;
		int b=10;
		boolean c;
		c=a<b;
		System.out.println("lessthan operator"+c);
		c=a>b;
		System.out.println("greaterthan operator"+c);
		c=a<=b;
		System.out.println("lessthan equal"+c);
		c=a>=b;
		System.out.println("greaterthan equal"+c);
		c=a==b;
		System.out.println("equal operator"+c);
	}

}
